import React, { useState } from 'react';

const Addcources =()=>{

const [name , setName] =useState({
  userid: "",
  title: "",
  description: "",
});
const [items, setItems] =useState([]);

const eventInput=(event)=>{ 
console.log(event.target.name);
console.log(event.target.value);


const {name, value} = event.target;
  setName((preValue)=>{
    console.log(preValue);
     return{
       ...preValue,
       [name]:value,
     }});
};



const  SubmitBtn=(event)=>{
  // event.preventDefault();
  // alert("form submit");
  setItems((preVal)=>{
    return[ 
      ...preVal,
      name
    ];
  });
};





    return(<>

    
    <div className="container ">
    <h1 className=" text-center"> Fill Cource Details {name.userid}
    <br />
    {name.title}<br />
    {name.description}</h1>
    <div className="row">
      <div className="col-md-8 col-10 mx-auto">
    <form onSubmit={SubmitBtn} action="/" >
    <div className="form-group">
      <label for="userid">Cource Id</label>
      <input type="number" className="form-control" placeholder="Enter Cource Id" id="userid" name="userid" onChange={eventInput} value={name.userid} />
    </div>
    <div className="form-group">
      <label for="pwd">Cource title</label>
      <input type="text" className="form-control" id="title" placeholder="Enter Cource Title" name="title" onChange={eventInput} value={name.title} />
    </div>
    <div class="form-group">
  <label for="description">Enter Cource Description</label>
  <textarea class="form-control" rows="3" placeholder="Enter Cource Description" id="description" onChange={eventInput} name="description" value={name.description}></textarea>
</div>
<div className="text-center">
<button type="submit" class="btn btn-outline-success">Add Cource</button>

</div>

  </form>
  <div> {
    items.map((val,index)=> <li>{val}</li>)
  }</div>
  </div>
  </div>
  </div>
    
    </>);
}

export default Addcources;
