import React, { useState, useEffect } from 'react';
import Cources from './Cources';
import Addcources from './Addcources';


const Allcource=()=>{
    useEffect(()=>{
        document.title="AllCourses || Learncodewith-Rahul Bisht"
        },[]);
    const [cources, setCources]=useState([
        // {title:"Java Cource", description:"It is a java cource for beginers."},
    ]);
         
const Submitbutton=(name)=>{
    setCources((preval)=>{
        return[...preval,name];
    });
    
};


return(<>
<div className="text-center">
    <h1> View Cources</h1>
    <p className="font-weight-bold">List  Of  all  Courses</p>

    {
        cources.map((val,index)=> <Cources userid={val.userid} title={val.title} description={val.description} key={index} id={index} Submitbtn={Submitbutton} />)
    }


    {/* {
        cources.length > 0 ? cources.map((val,index)=> <Cources props={val} key={index} id={index}  />) :  "No Cources Available" 
    } */}
</div>

</>);
}

export default Allcource;
